vsShader.program:
cbuffer cbPerObject
{
	float4x4 gWorldViewProj; 
};


struct VertexIn
{
	float3 Pos   : POSITION;
	float3 Nor :	NORMAL;
	//float4 Color : COLOR;
	float2 UV : TEXCOORD0;
};

struct VertexOut
{
	float4 PosH  : SV_POSITION;
	float depth : TEXCOORD0;
};

VertexOut VS(VertexIn vin)
{
	VertexOut vout;

	// Transform to homogeneous clip space.

	vout.PosH = mul(float4(vin.Pos, 1.0f), gWorldViewProj);
	vout.depth = vout.PosH.z;

	return vout;
}

psShader.program:

struct VertexOut
{
	float4 PosH  : SV_POSITION;
	float depth : TEXCOORD0;
};

float PS(VertexOut pin) : SV_TARGET
{
	return pin.depth;
}
